import './App.css';
import React, { useState } from 'react';

function App() {
  const [temperature,settemperature] = useState(0)
  const increment = ()=>{
    settemperature(temperature+1)
    if (temperature<=15 && temperature>-10) {
      document.getElementById('app').classList.remove('freezing')
      document.getElementById('app').classList.add('cold')

    } else if (temperature < 40 && temperature>=16) {
      document.getElementById('app').classList.remove('cold')
      document.getElementById('app').classList.add('neutral')
    }else if(temperature>40){

      document.getElementById('app').classList.remove('neutral')
      document.getElementById('app').classList.add('hot')
    }
  }
  const decrement = ()=>{
    settemperature(temperature-1)
    if (temperature >= 40) {
      document.getElementById('app').classList.add('hot')
    } else if (temperature < 40 && temperature>=17) {
      document.getElementById('app').classList.remove('hot')
      document.getElementById('app').classList.add('neutral')
    }else if(temperature<17 && temperature>-14){
      document.getElementById('app').classList.remove('neutral')
      document.getElementById('app').classList.add('cold')

    }else if(temperature<-10){
      document.getElementById('app').classList.remove('cold')
      document.getElementById('app').classList.add('freezing')
      document.getElementById('app').style.color='black'

    }
  }

  return (
  <>

    <div className="app-container" >
    <div className="temperature-display-container">
          <div className="temperature-display" id="app">
            {temperature}Â°C

      </div>
    </div>
    <div className="button-container">
    <button id="increment" onClick={increment}>+</button>
    <button id="decrement" onClick={decrement}>-</button>

    </div>

    </div>

  </>
  );
}

export default App;
